mapper.select:
	SELECT
		COUNT(*)
	FROM TB_SM02M01 A
	  LEFT OUTER JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = A.PRJCT_CD
	WHERE 1=1 

	 		AND OUT_CO_CD = #{coCd}


            AND TRST_DT BETWEEN #{strtDt} AND #{endDt}


	 		AND SELPCH_CD = #{selpchCd}


			AND PRDT_IMP_YN = #{prdtImpYn}


			AND A.PRJCT_CD = #{prjctCd}


	        AND SD05.PRJCT_NM LIKE '%' || #{prjctNm} || '%'

	ORDER BY TRST_NO

SELECT * FROM( SELECT ROWNUM AS RNUM, D.* FROM( SELECT A.TRST_NO ,A.OUT_CO_CD ,FN_CM05M01_CD_TO_NM(A.OUT_CO_CD) AS OUT_CO_NM ,A.OUT_WH_CD ,FN_CM05M01_CD_TO_NM(A.OUT_WH_CD) AS OUT_WH_NM ,A.OWNER_CD ,FN_CM05M01_CD_TO_NM(A.OWNER_CD) AS OWNER_NM ,A.TYP_CD ,FN_CM05M01_CD_TO_NM(A.TYP_CD) AS TYP_NM ,A.PRJCT_CD ,(SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM ,A.MAKR_CD ,FN_CM05M01_CD_TO_NM(A.MAKR_CD) AS MAKR_NM ,A.PRDT_CD ,C.PRDT_NM ,A.PRDT_SIZE ,A.PRDT_SPEC ,A.PRDT_LEN ,A.IMP_YN ,FN_CM05M01_CD_TO_NM(A.IMP_YN ) AS IMP_NM ,A.MOVE_QTY AS MO_WH ,A.STD_UPR ,A.IN_CO_CD ,FN_CM05M01_CD_TO_NM(A.IN_CO_CD) AS IN_CO_NM ,A.IN_TYP_CD ,FN_CM05M01_CD_TO_NM(A.IN_TYP_CD) AS IN_TYP_NM ,A.IN_WH_CD ,FN_CM05M01_CD_TO_NM(A.IN_WH_CD) AS IN_WH_NM ,A.IN_PRJCT_CD ,(SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.IN_PRJCT_CD ) AS IN_PRJCT_NM ,A.IN_PRDT_CD ,(SELECT PRDT_NM FROM TB_BM01M01 C WHERE C.PRDT_CD = A.IN_PRDT_CD) AS IN_PRDT_NM ,A.IN_PRDT_SIZE ,A.IN_PRDT_SPEC ,A.IN_PRDT_LEN ,TO_CHAR(TO_DATE(A.TRST_DT, 'YYYYMMDD'),'YYYY-MM-DD') AS TRST_DT ,A.UDT_DTTM ,TO_CHAR(A.CREAT_DTTM,'YYYY-MM-DD') AS CREAT_DTTM ,A.RMK ,A.TRANS_AMT ,A.TRANS_SEQ FROM TB_SM02M01 A, TB_BM01M01 C WHERE A.PRDT_CD = C.PRDT_CD
AND OUT_CO_CD = #{coCd}
AND TRST_DT BETWEEN #{strtDt} AND #{endDt}
AND OUT_WH_CD = #{outWhCd}
AND IN_WH_CD = #{inWhCd}
AND A.PRJCT_CD = #{prjctCd}
AND A.PRDT_CD = #{prdtCd}
AND C.PRDT_NM LIKE '%'||UPPER(#{prdtNm}) ||'%'
ORDER BY A.TRST_NO ) D WHERE 1= 1
AND ( D.PRJCT_NM LIKE '%' || #{prjctNm} || '%' OR D.IN_PRJCT_NM LIKE '%' || #{prjctNm} || '%' )
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT
	COUNT(*)
FROM TB_SM01M01 A
  INNER JOIN TB_BM01M01 C ON A.PRDT_CD = C.PRDT_CD
  INNER JOIN TB_BM02M01 E ON  A.CLNT_CD = E.CLNT_CD
  LEFT OUTER JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = A.PRJCT_CD
WHERE 1=1 

	AND A.CO_CD = #{coCd}


 	AND A.WH_CD = #{whCd}


	AND E.CLNT_NM LIKE '%' || #{clntNm} || '%'


	AND OWNER_CD = #{ownerCd}


	AND TYP_CD = #{typCd}





	AND IMP_YN = #{impYn}


		AND C.PRDT_NM LIKE '%' || #{prdtNm} || '%'


		AND C.PRDT_COIL_YN LIKE  #{prdtCoilYn}


		AND SD05.PRJCT_NM LIKE '%' || #{prjctNm} || '%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, D.* FROM( SELECT A.STOK_SEQ , A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , A.WH_CD , FN_CM05M01_CD_TO_NM(A.WH_CD) AS WH_NM , A.OWNER_CD , FN_CM05M01_CD_TO_NM(A.OWNER_CD) AS OWNER_NM , A.TYP_CD , FN_CM05M01_CD_TO_NM(A.TYP_CD) AS TYP_NM , A.MAKR_CD , FN_CM05M01_CD_TO_NM(A.MAKR_CD) AS MAKR_NM , A.PRJCT_CD , (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM , A.PRDT_CD , C.PRDT_NM , A.TYP_CD , FN_CM05M01_CD_TO_NM(A.TYP_CD) AS TYP_NM , A.CLNT_CD , A.TYP_CD , FN_CM05M01_CD_TO_NM(A.TYP_CD) AS TYP_NM , A.PRDT_SPEC , A.PRDT_SIZE , A.PRDT_LEN , E.CLNT_NM , A.IMP_YN , FN_CM05M01_CD_TO_NM(A.IMP_YN) AS IMP_NM , A.STOCK_QTY , A.STOCK_WT , A.STOCK_UPR , A.STOCK_AMT , A.STD_UPR , A.PCHS_UPR , A.SELL_UPR , A.SELL_LAST_DT , A.PROPRT_STOCK_QTY , A.ODR_QTY , A.STOCK_CHG_CD , C.PRDT_COIL_YN , C.PRDT_CNVRSN_WT FROM TB_SM01M01 A LEFT JOIN TB_BM01M01 C ON A.PRDT_CD = C.PRDT_CD LEFT JOIN TB_BM02M01 E ON A.CLNT_CD = E.CLNT_CD
AND CO_CD = #{coCd}
AND WH_CD = #{whCd}
AND E.CLNT_NM LIKE '%' || #{clntNm} || '%'
AND OWNER_CD = #{ownerCd}
AND TYP_CD = #{typCd}
AND IMP_YN = #{impYn}
AND C.PRDT_NM LIKE '%' || #{prdtNm} || '%'
AND C.PRDT_COIL_YN LIKE #{prdtCoilYn}
AND A.PRJCT_CD = #{prjctCd}
ORDER BY C.PRDT_NM,A.PRJCT_CD )D WHERE 1= 1
AND D.PRJCT_NM LIKE '%' || #{prjctNm} || '%'
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:
UPDATE TB_SM01M01 SET
	STOCK_QTY = #{stockQty}-#{moveQty}
   ,STOCK_WT  = #{stockWt}-#{moveWt}
WHERE STOK_SEQ = #{stokSeq}
