mapper.select:
SELECT COUNT(*) FROM TB_SM01M01 SM01 INNER JOIN TB_BM01M01 BM01 ON SM01.PRDT_CD = BM01.PRDT_CD LEFT JOIN TB_BM02M01 BM02 ON SM01.CLNT_CD = BM02.CLNT_CD INNER JOIN TB_CM05M01 CM05 ON SM01.WH_CD = CM05.CODE_ID WHERE CM05.CODE_RPRC = 'WHDIV_03' AND SM01.STOCK_QTY != 0
AND SM01.CO_CD = #{coCd}
AND SM01.WH_CD = #{whCd}
AND SM01.OWNER_CD = #{ownerCd}
AND SM01.TYP_CD = #{typCd}
AND BM01.PRDT_NM LIKE '%' || #{prdtNm} || '%'
AND BM02.CLNT_NM LIKE '%' || #{clntNm} || '%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM( SELECT SM01.CO_CD , FN_CM05M01_CD_TO_NM(SM01.CO_CD) AS CO_NM , SM01.WH_CD , FN_CM05M01_CD_TO_NM(SM01.WH_CD) AS WH_NM , SM01.OWNER_CD , FN_CM05M01_CD_TO_NM(SM01.OWNER_CD) AS OWNER_NM , SM01.TYP_CD , FN_CM05M01_CD_TO_NM(SM01.TYP_CD) AS TYP_NM , SM01.MAKR_CD , FN_CM05M01_CD_TO_NM(SM01.MAKR_CD) AS MAKR_NM , SM01.PRDT_CD , BM01.PRDT_NM , SM01.IMP_YN , FN_CM05M01_CD_TO_NM(SM01.IMP_YN) AS IMP_NM , SM01.STOCK_CHG_CD , FN_CM05M01_CD_TO_NM(SM01.STOCK_CHG_CD) AS STOCK_CHG_NM , SM01.STOCK_AMT , SM01.STOCK_QTY , SM01.STOCK_WT , SM01.STOCK_UPR , SM01.STD_UPR , SM01.PCHS_UPR , SM01.SELL_UPR , BM02.CLNT_NM , TO_CHAR(SM01.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM , CM05.CODE_RPRC FROM TB_SM01M01 SM01 INNER JOIN TB_BM01M01 BM01 ON SM01.PRDT_CD = BM01.PRDT_CD LEFT JOIN TB_BM02M01 BM02 ON SM01.CLNT_CD = BM02.CLNT_CD INNER JOIN TB_CM05M01 CM05 ON SM01.WH_CD = CM05.CODE_ID WHERE CM05.CODE_RPRC = 'WHDIV_03' AND SM01.STOCK_QTY != 0
AND SM01.CO_CD = #{coCd}
AND SM01.WH_CD = #{whCd}
AND SM01.OWNER_CD = #{ownerCd}
AND SM01.TYP_CD = #{typCd}
AND BM01.PRDT_NM LIKE '%' || #{prdtNm} || '%'
AND BM02.CLNT_NM LIKE '%' || #{clntNm} || '%'
ORDER BY UDT_DTTM DESC ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
