mapper.select:
		SELECT
			COUNT(*)
		FROM TB_SM03M01 
		WHERE 1=1

 			AND CO_CD = #{coCd}


 			AND ACINS_DT = #{acinsDt}


 			AND WH_CD = #{whCd}


 			AND PRDT_CD = #{prdtCd}


 			AND PRJCT_CD = #{prjctCd}


 			AND OWNER_CD = #{ownerCd}


 			AND TYP_CD = #{typCd}


 			AND MAKR_CD = #{makrCd}


 			AND CLNT_CD = #{clntCd}


 			AND IMP_YN = #{impYn}

SELECT * FROM( SELECT ROWNUM AS RNUM, D.* FROM( SELECT A.CO_CD, A.ACINS_DT, A.WH_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.WH_CD) AS WH_NM, A.PRDT_CD, (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = A.PRDT_CD) AS PRDT_NM, A.OWNER_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.OWNER_CD) AS OWNER_NM, A.CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = A.CLNT_CD) AS CLNT_NM, A.TYP_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.TYP_CD) AS TYP_NM, A.MAKR_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.MAKR_CD) AS MAKR_NM, A.PRJCT_CD, (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD) AS PRJCT_NM, A.IMP_YN, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.IMP_YN) AS IMP_NM, A.STOCK_QTY, A.ACINS_QTY, A.STOCK_QTY - A.ACINS_QTY AS STOCK_ACINS_QTY, A.RMK FROM TB_SM03M01 A WHERE 1=1
AND A.CO_CD = #{coCd}
AND A.ACINS_DT = #{acinsDt}
AND A.WH_CD = #{whCd}
AND A.PRDT_CD = #{prdtCd}
AND A.PRJCT_CD = #{prjctCd}
AND A.OWNER_CD = #{ownerCd}
AND A.TYP_CD = #{typCd}
AND A.MAKR_CD = #{makrCd}
AND A.CLNT_CD = #{clntCd}
AND A.IMP_YN = #{impYn}
) D ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT T.*,
	(SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = T.CO_CD) AS CO_NM,	
	(SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = T.WH_CD) AS WH_NM,
	(SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = T.PRDT_CD) AS PRDT_NM,
	(SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = T.OWNER_CD) AS OWNER_NM,
	(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.CLNT_CD) AS CLNT_NM,
	(SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = T.TYP_CD) AS TYP_NM,
	(SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = T.MAKR_CD) AS MAKR_NM,
	(SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = T.PRJCT_CD) AS PRJCT_NM
FROM TB_SM03M01 T
WHERE 1=1
	AND	CO_CD = #{coCd}
	AND ACINS_DT = #{acinsDt}
	AND WH_CD = #{whCd}
	AND PRJCT_CD = #{prjctCd}
	AND PRDT_CD = #{prdtCd}
	AND OWNER_CD = #{ownerCd}
	AND CLNT_CD = #{clntCd}
	AND TYP_CD = #{typCd}
	AND MAKR_CD = #{makrCd}
	AND PRJCT_CD = #{prjctCd}
	AND IMP_YN = #{impYn}

mapper.update:
UPDATE TB_SM03M01
   SET 
	 ACINS_QTY		= #{acinsQty}
	,RMK			= #{rmk}
	,UDT_ID         = #{userId}
	,UDT_PGM        = #{pgmId}										
	,UDT_DTTM       = SYSDATE
WHERE
	  CO_CD = #{coCd}
  AND ACINS_DT = TO_CHAR(TO_DATE(#{acinsDt},'YYYY-MM-DD'),'YYYYMMDD')
  AND WH_CD = #{whCd}
  AND PRDT_CD = #{prdtCd}
  AND OWNER_CD = #{ownerCd}
  AND CLNT_CD = #{clntCd}
  AND TYP_CD = #{typCd}
  AND MAKR_CD = #{makrCd}
  AND PRJCT_CD = #{prjctCd}
  AND IMP_YN = #{impYn}

mapper.delete:
 DELETE FROM TB_SM03M01
  WHERE CO_CD = #{coCd}
	AND ACINS_DT = #{acinsDt}
	AND WH_CD = #{whCd}
	AND PRJCT_CD = #{prjctCd}
	AND PRDT_CD = #{prdtCd}
	AND OWNER_CD = #{ownerCd}
	AND CLNT_CD = #{clntCd}
	AND TYP_CD = #{typCd}
	AND MAKR_CD = #{makrCd}
	AND PRJCT_CD = #{prjctCd}
	AND IMP_YN = #{impYn}
