mapper.select:
		SELECT
			COUNT(*)
		FROM TB_SM03M01 
		WHERE 1=1

 			AND CO_CD = #{coCd}


 			AND ACINS_DT = #{acinsDt}


 			AND WH_CD = #{whCd}


 			AND PRDT_CD = #{prdtCd}


 			AND PRJCT_CD = #{prjctCd}


 			AND OWNER_CD = #{ownerCd}


 			AND TYP_CD = #{typCd}


 			AND MAKR_CD = #{makrCd}


 			AND CLNT_CD = #{clntCd}


 			AND IMP_YN = #{impYn}

SELECT * FROM( SELECT ROWNUM AS RNUM, D.* FROM( SELECT A.CO_CD, A.ACINS_DT, A.STOK_SEQ, A.WH_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.WH_CD) AS WH_NM, A.OWNER_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.OWNER_CD) AS OWNER_NM, A.CLNT_CD, BM02.CLNT_NM, --- (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = A.CLNT_CD) AS CLNT_NM, A.TYP_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.TYP_CD) AS TYP_NM, A.MAKR_CD, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.MAKR_CD) AS MAKR_NM, A.PRJCT_CD, SD05.PRJCT_NM, -- (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD) AS PRJCT_NM, A.PRDT_CD, BM01.PRDT_NM, --- (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = A.PRDT_CD) AS PRDT_NM, A.PRDT_SIZE, A.PRDT_SPEC, A.PRDT_LEN, A.IMP_YN, (SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = A.IMP_YN) AS IMP_NM, A.STOCK_QTY, A.ACINS_QTY, A.STOCK_QTY - A.ACINS_QTY AS STOCK_ACINS_QTY, A.ACINS_ID, (SELECT NAME FROM TB_CM06M01 C WHERE C.ID = A.ACINS_ID) AS ACINS_NM, A.RMK FROM TB_SM03M01 A INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = A.PRDT_CD LEFT OUTER JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = A.CLNT_CD LEFT OUTER JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = A.PRJCT_CD WHERE 1=1
AND A.CO_CD = #{coCd}
AND A.ACINS_DT = #{acinsDt}
AND A.WH_CD = #{whCd}
AND A.PRDT_CD = #{prdtCd}
AND A.PRJCT_CD = #{prjctCd}
AND A.OWNER_CD = #{ownerCd}
AND A.TYP_CD = #{typCd}
AND A.MAKR_CD = #{makrCd}
AND A.CLNT_CD = #{clntCd}
AND A.IMP_YN = #{impYn}
AND BM01.PRDT_NM like '%' || UPPER(#{prdtNm}) ||'%'
AND BM02.CLNT_NM like '%' || #{clntNm} ||'%'
AND SD05.PRJCT_NM like '%' || #{prjctNm} ||'%'
) D ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

mapper.update:
UPDATE TB_SM03M01
   SET 
	 ACINS_QTY		= #{acinsQty}
	,RMK			= #{rmk}
	,UDT_ID         = #{userId}
	,UDT_PGM        = #{pgmId}										
	,UDT_DTTM       = SYSDATE
WHERE
	  CO_CD    = #{coCd}
  AND ACINS_DT = TO_CHAR(TO_DATE(#{acinsDt},'YYYY-MM-DD'),'YYYYMMDD')
  AND STOK_SEQ = #{stokSeq}

mapper.delete:
 DELETE FROM TB_SM03M01
  WHERE CO_CD    = #{coCd}
	AND ACINS_DT = #{acinsDt}
	AND STOK_SEQ = #{stokSeq}

mapper.insert:
INSERT INTO TB_SM03M01 ( CO_CD ,ACINS_DT ,STOK_SEQ ,WH_CD ,OWNER_CD ,CLNT_CD ,TYP_CD ,MAKR_CD ,PRJCT_CD ,PRDT_CD ,PRDT_SIZE ,PRDT_SPEC ,PRDT_LEN ,IMP_YN ,STOCK_QTY ,ACINS_QTY ,ACINS_ID ,CREAT_PGM ,CREAT_DTTM ,UDT_ID ,UDT_PGM ) SELECT CO_CD ,TO_CHAR(TO_DATE(#{acinsDt},'YYYY-MM-DD'),'YYYYMMDD') ,STOK_SEQ ,WH_CD ,OWNER_CD ,CLNT_CD ,TYP_CD ,MAKR_CD ,PRJCT_CD ,PRDT_CD ,PRDT_SIZE ,PRDT_SPEC ,PRDT_LEN ,IMP_YN ,STOCK_QTY ,'0' ,#{userId} ,#{pgmId} ,SYSDATE ,#{userId} ,#{pgmId} FROM TB_SM01M01 WHERE CO_CD = #{coCd}
AND WH_CD = #{whCd}
AND RDT_CD = #{prdtCd}
AND PRJCT_CD = #{prjctCd}
AND OWNER_CD = #{ownerCd}
AND TYP_CD = #{typCd}
AND CLNT_CD = #{clntCd}
AND IMP_YN = #{impYn}
