SELECT COUNT(*) FROM( SELECT ROWNUM AS RNUM, D.* FROM( SELECT T.CO_CD ,T.TRST_CLNT_CD ,T.CLNT_NM
,T.SALES_MNG ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM
,T.PRDT_DIV ,SUM(BILG_WT) AS BILG_WT ,SUM(BILG_AMT) AS BILG_AMT ,SUM(BILG_VAT_AMT) AS BILG_VAT_AMT ,SUM(BILG_AMT + BILG_VAT_AMT) AS TOTAL_AMT
FROM TB_AR02M01 T LEFT OUTER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = T.TRST_PRDT_CD LEFT OUTER JOIN TB_SD09M01 SD09 ON SD09.SITE_CD = T.SITE_CD WHERE 1=1
AND T.CO_CD = #{coCd}
AND TRST_DT BETWEEN TO_CHAR(TO_DATE(#{strtDt}, 'YYYY-MM-DD'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{endDt}, 'YYYY-MM-DD'), 'YYYYMMDD')
AND TRST_CLNT_CD = #{clntCd}
AND CLNT_NM LIKE '%' || #{clntNm} || '%'
AND TRSP_RMK LIKE '%' || #{trspRmk} || '%'
AND T.SALES_MNG = #{salesMng}
AND T.PRDT_DIV = #{prdtDiv}
AND T.PRDT_STKN = #{prdtStkn}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09.SITE_NM like '%'|| #{siteNm} || '%'
AND T.SELPCH_CD = #{selpch}
AND T.BILG_CERT_NO = #{bilgCertNo_S}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND BM01.PRDT_GRP = #{prdtGrp}
AND TAXIVC_COPRT = #{taxivcCoprt}
AND T.TYP_CD = #{typCd}
AND CLNT_NM NOT LIKE '%금문철강%'
AND CLNT_NM NOT LIKE '%지지엠%'
AND CLNT_NM NOT LIKE '%지지스틸%'
GROUP BY T.CO_CD, TRST_CLNT_CD, CLNT_NM
,SALES_MNG
,T.PRDT_DIV ORDER BY T.CO_CD, TRST_CLNT_CD ) D
D.SALES_MNG_NM LIKE '%' || #{salesMngNm} ||'%'
)

SELECT * FROM( SELECT ROWNUM AS RNUM, D.* FROM( SELECT T.CO_CD ,FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM ,T.TRST_CLNT_CD --,(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.TRST_CLNT_CD) AS CLNT_NM ,FN_CM05M01_CD_TO_NM((SELECT CLNT_DIV_CD FROM TB_BM02M01 WHERE CLNT_CD = T.TRST_CLNT_CD)) AS CLNT_DIV_NM ,T.CLNT_NM
,T.SALES_MNG ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM
,T.PRDT_DIV ,FN_CM05M01_CD_TO_NM(T.PRDT_DIV) AS PRDT_DIV_NM ,SUM(BILG_WT) AS BILG_WT ,SUM(BILG_AMT) AS BILG_AMT ,SUM(BILG_VAT_AMT) AS BILG_VAT_AMT ,SUM(BILG_AMT + BILG_VAT_AMT) AS TOTAL_AMT
FROM TB_AR02M01 T LEFT OUTER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = T.TRST_PRDT_CD LEFT OUTER JOIN TB_SD09M01 SD09 ON SD09.SITE_CD = T.SITE_CD WHERE 1=1
AND T.CO_CD = #{coCd}
AND TRST_DT BETWEEN TO_CHAR(TO_DATE(#{strtDt}, 'YYYY-MM-DD'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{endDt}, 'YYYY-MM-DD'), 'YYYYMMDD')
AND TRST_CLNT_CD = #{clntCd}
AND CLNT_NM LIKE '%' || #{clntNm} || '%'
AND TRSP_RMK LIKE '%' || #{trspRmk} || '%'
AND T.SALES_MNG = #{salesMng}
AND T.PRDT_DIV = #{prdtDiv}
AND T.PRDT_STKN = #{prdtStkn}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09.SITE_NM like '%'|| #{siteNm} || '%'
AND T.SELPCH_CD = #{selpch}
AND T.BILG_CERT_NO = #{bilgCertNo_S}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND BM01.PRDT_GRP = #{prdtGrp}
AND TAXIVC_COPRT = #{taxivcCoprt}
AND T.TYP_CD = #{typCd}
AND CLNT_NM NOT LIKE '%금문철강%'
AND CLNT_NM NOT LIKE '%지지엠%'
AND CLNT_NM NOT LIKE '%지지스틸%'
GROUP BY T.CO_CD , TRST_CLNT_CD, CLNT_NM
,SALES_MNG
,T.PRDT_DIV ORDER BY T.CO_CD,TRST_CLNT_CD ) D
D.SALES_MNG_NM LIKE '%' || #{salesMngNm} ||'%'
) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

		SELECT
			 BILG_CERT_NO
		FROM TB_AR02M01 T
		WHERE 1=1

 			AND CO_CD = #{coCd}


 			AND TRST_DT BETWEEN TO_CHAR(TO_DATE(#{strtDt}, 'YYYY-MM-DD'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{endDt}, 'YYYY-MM-DD'), 'YYYYMMDD')


 			AND TRST_CLNT_CD = #{clntCd}


 			AND TRSP_RMK LIKE '%' || #{trspRmk} || '%'


			AND SALES_MNG = #{salesMng}


			AND PRDT_DIV = #{prdtDiv}


			AND PRDT_STKN = #{prdtStkn}


 			AND SELPCH_CD = #{selpch}


 			AND PRJCT_CD = #{prjctCd}


 			AND BILG_CERT_NO = #{bilgCertNo_S}

			AND BILG_CERT_NO IS NOT NULL

		SELECT
			 BILG_CERT_NO
		FROM TB_AR02M01 T
		WHERE 1=1

 			AND CO_CD = #{coCd}


 			AND TRST_DT BETWEEN TO_CHAR(TO_DATE(#{strtDt}, 'YYYY-MM-DD'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{endDt}, 'YYYY-MM-DD'), 'YYYYMMDD')


 			AND TRST_CLNT_CD = #{clntCd}


 			AND TRSP_RMK LIKE '%' || #{trspRmk} || '%'


