mapper.select:
SELECT COUNT(*)
FROM (SELECT  A.CO_CD 
	      , A.PRJCT_CD
  		  , (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM
	FROM TB_SM01M01 A, TB_BM01M01 C, TB_BM02M01 E
	WHERE  A.PRDT_CD = C.PRDT_CD 
			AND A.CLNT_CD = E.CLNT_CD(+)
			AND A.STOCK_QTY != 0

			AND A.CO_CD = #{coCd}


			 		AND A.WH_CD = #{whCd}


					AND A.OWNER_CD = #{ownerCd}


					AND A.TYP_CD = #{typCd}





					AND C.PRDT_NM LIKE '%' || #{prdtNm} || '%'


					AND A.PRJCT_CD =  #{prjctCd}


					AND E.CLNT_NM LIKE '%' || #{clntNm} || '%'


				AND C.PRDT_DIV = #{prdtDiv}


				AND C.PRDT_GRP = #{prdtGrp}


	) D


		   PRJCT_NM like '%' || #{prjctNm} || '%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, D.* FROM( SELECT A.STOK_SEQ , A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , A.WH_CD , FN_CM05M01_CD_TO_NM(A.WH_CD) AS WH_NM , A.OWNER_CD , FN_CM05M01_CD_TO_NM(A.OWNER_CD) AS OWNER_NM , A.TYP_CD , FN_CM05M01_CD_TO_NM(A.TYP_CD) AS TYP_NM , A.MAKR_CD , FN_CM05M01_CD_TO_NM(A.MAKR_CD) AS MAKR_NM , A.PRJCT_CD , (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM , A.PRDT_CD , C.PRDT_NM , A.IMP_YN , FN_CM05M01_CD_TO_NM(A.IMP_YN) AS IMP_NM , A.STOCK_CHG_CD , FN_CM05M01_CD_TO_NM(A.STOCK_CHG_CD) AS STOCK_CHG_NM , A.STOCK_QTY , A.STOCK_WT , A.STOCK_UPR , (A.STOCK_QTY*A.STOCK_UPR) as STOCK_AMT , A.STD_UPR , A.PCHS_UPR , A.SELL_UPR , A.CLNT_CD , E.CLNT_NM , A.PRDT_SIZE , A.PRDT_SPEC , A.PRDT_LEN , C.PRDT_STKN , C.PRDT_CNVRSN_WT , C.PRDT_STOCK_CD , C.PRDT_DIV , FN_CM05M01_CD_TO_NM(C.PRDT_STKN) AS PRDT_STKN_NM , TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM FROM TB_SM01M01 A, TB_BM01M01 C, TB_BM02M01 E WHERE A.PRDT_CD = C.PRDT_CD AND A.CLNT_CD = E.CLNT_CD(+) AND A.STOCK_QTY != 0
AND A.CO_CD = #{coCd}
AND A.WH_CD = #{whCd}
AND A.OWNER_CD = #{ownerCd}
AND A.TYP_CD = #{typCd}
AND C.PRDT_NM LIKE '%' || #{prdtNm} || '%'
AND A.PRJCT_NM = #{prjctNm}
AND E.CLNT_NM LIKE '%' || #{clntNm} || '%'
AND C.PRDT_DIV = #{prdtDiv}
AND C.PRDT_GRP = #{prdtGrp}
ORDER BY UDT_DTTM DESC )D
PRJCT_NM like '%' || #{prjctNm} || '%'
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(*)
FROM (SELECT A.CO_CD 
	      , A.PRJCT_CD
  		  , (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM
	FROM TB_SM01H01 A, TB_BM01M01 C, TB_BM02M01 E
	WHERE  A.PRDT_CD = C.PRDT_CD AND A.CLNT_CD = E.CLNT_CD(+)

			AND A.CO_CD = #{coCd}


			 		AND A.WH_CD = #{whCd}


					AND A.OWNER_CD = #{ownerCd}


					AND A.TYP_CD = #{typCd}





					AND C.PRDT_NM LIKE '%' || UPPER(#{prdtNm}) || '%'


					AND E.CLNT_NM LIKE '%' || #{clntNm} || '%'


					AND A.PRJCT_CD =  #{prjctCd}


					 AND TO_CHAR(A.PROC_DTTM, 'YYYYMMDD')  <=  REPLACE(#{procDttm}, '-', '') 


				AND C.PRDT_GRP = #{prdtGrp}


	) D		


		   PRJCT_NM like '%' || #{prjctNm} || '%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, D.* FROM( SELECT A.STOK_SEQ , A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , A.WH_CD , FN_CM05M01_CD_TO_NM(A.WH_CD) AS WH_NM , A.OWNER_CD , FN_CM05M01_CD_TO_NM(A.OWNER_CD) AS OWNER_NM , A.TYP_CD , FN_CM05M01_CD_TO_NM(A.TYP_CD) AS TYP_NM , A.MAKR_CD , FN_CM05M01_CD_TO_NM(A.MAKR_CD) AS MAKR_NM , A.PRJCT_CD , (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM , A.PRDT_CD , C.PRDT_NM , A.IMP_YN , FN_CM05M01_CD_TO_NM(A.IMP_YN) AS IMP_NM , A.CHG_CD , FN_CM05M01_CD_TO_NM(A.CHG_CD) AS CHG_NM , A.CHG_QTY , A.STOCK_CHG_CD , FN_CM05M01_CD_TO_NM(A.STOCK_CHG_CD) AS STOCK_CHG_NM , A.STOCK_UPR , A.STOCK_QTY , (NVL(A.STOCK_UPR,0) * NVL(A.STOCK_QTY,0)) AS STOCK_AMT , A.STOCK_WT , A.STD_UPR , A.PCHS_UPR , A.SELL_UPR , E.CLNT_NM , A.PRDT_SIZE , A.PRDT_SPEC , A.PRDT_LEN , C.PRDT_STKN , C.PRDT_CNVRSN_WT , C.PRDT_STOCK_CD , C.PRDT_DIV , FN_CM05M01_CD_TO_NM(C.PRDT_STKN) AS PRDT_STKN_NM , TO_CHAR(A.PROC_DTTM, 'YYYY-MM-DD') AS PROC_DTTM FROM TB_SM01H01 A, TB_BM01M01 C, TB_BM02M01 E WHERE A.PRDT_CD = C.PRDT_CD AND A.CLNT_CD = E.CLNT_CD(+)
AND A.CO_CD = #{coCd}
AND A.WH_CD = #{whCd}
AND A.OWNER_CD = #{ownerCd}
AND A.TYP_CD = #{typCd}
AND C.PRDT_NM LIKE '%' || UPPER(#{prdtNm}) || '%'
AND A.PRJCT_CD = #{prjctCd}
AND E.CLNT_NM LIKE '%' || #{clntNm} || '%'
AND TO_CHAR(A.PROC_DTTM, 'YYYYMMDD') <= REPLACE(#{procDttm}, '-', '')
AND C.PRDT_GRP = #{prdtGrp}
ORDER BY A.PRDT_CD, A.WH_CD, A.TYP_CD, A.OWNER_CD, A.CLNT_CD, PROC_DTTM DESC, A.TRST_NO DESC )D
PRJCT_NM like '%' || #{prjctNm} || '%'
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT A.STOK_SEQ , A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , A.WH_CD , FN_CM05M01_CD_TO_NM(A.WH_CD) AS WH_NM , A.OWNER_CD , FN_CM05M01_CD_TO_NM(A.OWNER_CD) AS OWNER_NM , A.TYP_CD , FN_CM05M01_CD_TO_NM(A.TYP_CD) AS TYP_NM , A.MAKR_CD , FN_CM05M01_CD_TO_NM(A.MAKR_CD) AS MAKR_NM , A.PRJCT_CD , (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM , A.PRDT_CD , C.PRDT_NM , A.IMP_YN , FN_CM05M01_CD_TO_NM(A.IMP_YN) AS IMP_NM , A.STOCK_CHG_CD , FN_CM05M01_CD_TO_NM(A.STOCK_CHG_CD) AS STOCK_CHG_NM , A.STOCK_AMT , A.STOCK_QTY , A.STOCK_WT , A.STOCK_UPR , A.STD_UPR , A.PCHS_UPR , A.SELL_UPR , E.CLNT_NM , A.PRDT_SIZE , A.PRDT_SPEC , A.PRDT_LEN , C.PRDT_STKN , C.PRDT_CNVRSN_WT , C.PRDT_STOCK_CD , C.PRDT_DIV , FN_CM05M01_CD_TO_NM(C.PRDT_STKN) AS PRDT_STKN_NM , TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM FROM TB_SM01M01 A, TB_BM01M01 C, TB_BM02M01 E WHERE A.PRDT_CD = C.PRDT_CD AND A.CLNT_CD = E.CLNT_CD(+) AND A.CO_CD = #{coCd} AND A.WH_CD = #{whCd} AND A.OWNER_CD = #{ownerCd} AND A.TYP_CD = #{typCd}
AND A.PRJCT_CD = #{prjctCd}
AND A.IMP_YN = #{impYn} AND C.PRDT_CD = #{prdtCd} AND E.CLNT_CD = #{clntCd}
AND A.PRDT_SIZE = #{prdtSize}
AND A.PRDT_SPEC = #{prdtSpec}
AND A.PRDT_LEN = #{mngPrdtLen}
AND A.PRDT_LEN = #{prdtLen}

mapper.update:
MERGE INTO TB_SM01M01 A USING DUAL ON ( CO_CD = #{coCd} AND WH_CD = #{whCd} AND PRDT_CD = #{prdtCd} AND OWNER_CD = #{ownerCd} AND CLNT_CD = #{clntCd} AND TYP_CD = #{typCd}
AND PRJCT_CD = #{prjctCd} AND IMP_YN = #{impYn}
AND PRDT_SIZE = #{prdtSize}
AND PRDT_SPEC = #{prdtSpec}
AND A.PRDT_LEN = #{mngPrdtLen}
AND A.PRDT_LEN = #{prdtLen}
) WHEN MATCHED THEN UPDATE SET STOCK_QTY = #{stockQty}, STOCK_WT = #{stockWt}, SELL_UPR = #{sellUpr}, STOCK_CHG_CD = #{stockChgCd}, UDT_ID = #{userId}, UDT_PGM = #{pgmId}, UDT_DTTM = SYSDATE WHEN NOT MATCHED THEN INSERT ( STOK_SEQ , CO_CD, WH_CD, PRDT_CD, OWNER_CD, CLNT_CD, TYP_CD, MAKR_CD, PRJCT_CD, IMP_YN, STOCK_QTY, STOCK_WT, STOCK_UPR, STD_UPR, PCHS_UPR, SELL_UPR, STOCK_CHG_CD, CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UDT_PGM, UDT_DTTM, PRDT_SIZE, PRDT_SPEC, PRDT_LEN ) VALUES( TB_SM01M01_SQ01.NEXTVAL, #{coCd}, #{whCd}, #{prdtCd}, #{ownerCd}, #{clntCd}, #{typCd}, DECODE(#{impYn},'Y','MAKR10','MAKR09'), #{prjctCd}, #{impYn}, #{stockQty}, #{stockWt}, #{stockUpr}, #{stdUpr}, #{pchsUpr}, #{sellUpr}, #{stockChgCd}, #{userId}, #{pgmId}, SYSDATE, #{userId}, #{pgmId}, SYSDATE, #{prdtSize, jdbcType=VARCHAR}, #{prdtSpec, jdbcType=VARCHAR},
#{mngPrdtLen}
#{prdtLen}
0
)

UPDATE TB_SM01M01
SET STOCK_QTY = #{stockQty}
   ,STOCK_WT  = #{stockWt}
WHERE   CO_CD     = #{coCd}
	AND WH_CD     = #{whCd}
	AND PRDT_CD   = #{prdtCd}
	AND OWNER_CD  = #{ownerCd}
	AND CLNT_CD   = #{clntCd}
	AND TYP_CD    = #{typCd}

	AND PRJCT_CD  = #{prjctCd}
	AND IMP_YN    = #{impYn}

		AND PRDT_SIZE = #{prdtSize}


		AND PRDT_SPEC = #{prdtSpec}	



			AND PRDT_LEN  = #{mngPrdtLen}


			AND PRDT_LEN  = #{prdtLen}
